settings.custom_liquid:
{% if localization.language.iso_code == 'de' %}{% if product.metafields.nutrition.energy %}
<small>
<strong>Nährwerte pro 100g:</strong>
<ul>

<li>Energie: {{ product.metafields.nutrition.energy }} KJ / {{ product.metafields.nutrition.energy | divided_by: 4.184 | floor }} KCal</li>

<li>Fett: {{ product.metafields.nutrition.fat.value.value }}{{ product.metafields.nutrition.fat.value.unit }}</li>

{% if product.metafields.nutrition.saturated_fat %}
<li>davon gesättigte Fettsäuren: {{ product.metafields.nutrition.saturated_fat.value.value }}{{ product.metafields.nutrition.saturated_fat.value.unit }}</li>
{% endif %}

<li>Kohlenhydrate: {{ product.metafields.nutrition.carbohydrate.value.value }}{{ product.metafields.nutrition.carbohydrate.value.unit }}</li>

{% if product.metafields.nutrition.sugar%}
<li>davon Zucker: {{ product.metafields.nutrition.sugar.value.value }}{{ product.metafields.nutrition.sugar.value.unit }}</li>
{% endif %}

{% if product.metafields.nutrition.fiber %}
<li>Ballaststoffe: {{ product.metafields.nutrition.fiber.value.value }}{{ product.metafields.nutrition.fiber.value.unit }}</li>
{% endif %}

<li>Eiweiss: {{ product.metafields.nutrition.protein.value.value }}{{ product.metafields.nutrition.protein.value.unit }}</li>

{% if product.metafields.nutrition.salt %}
<li>Salz: {{ product.metafields.nutrition.salt.value.value }}{{ product.metafields.nutrition.salt.value.unit }}</li>
{% endif %}

</ul>
</small>
{% endif %}{% else %}{% if product.metafields.nutrition.energy %}
<small>
<strong>Nutrition per 100g:</strong>
<ul>

<li>Energy: {{ product.metafields.nutrition.energy }} KJ / {{ product.metafields.nutrition.energy | divided_by: 4.184 | floor }} KCal</li>

<li>Fat: {{ product.metafields.nutrition.fat.value.value }}{{ product.metafields.nutrition.fat.value.unit }}</li>

{% if product.metafields.nutrition.saturated_fat %}
<li>Saturated fat: {{ product.metafields.nutrition.saturated_fat.value.value }}{{ product.metafields.nutrition.saturated_fat.value.unit }}</li>
{% endif %}

<li>Carbohydrates: {{ product.metafields.nutrition.carbohydrate.value.value }}{{ product.metafields.nutrition.carbohydrate.value.unit }}</li>

{% if product.metafields.nutrition.sugar%}
<li>Sugar: {{ product.metafields.nutrition.sugar.value.value }}{{ product.metafields.nutrition.sugar.value.unit }}</li>
{% endif %}

{% if product.metafields.nutrition.fiber %}
<li>Fiber: {{ product.metafields.nutrition.fiber.value.value }}{{ product.metafields.nutrition.fiber.value.unit }}</li>
{% endif %}

<li>Protein: {{ product.metafields.nutrition.protein.value.value }}{{ product.metafields.nutrition.protein.value.unit }}</li>

{% if product.metafields.nutrition.salt %}
<li>Salt: {{ product.metafields.nutrition.salt.value.value }}{{ product.metafields.nutrition.salt.value.unit }}</li>
{% endif %}

</ul>
</small>
{% endif %}{% endif %}
